function Layers(map) {
  // Snow depth as circle size
  map.addLayer({
    id: "snow-depth",
    type: "circle",
    source: "frost-live",
    paint: {
      "circle-radius": ["interpolate", ["linear"], ["get", "snow_depth"], 0, 2, 100, 20],
      "circle-color": "white",
      "circle-opacity": 0.7,
    },
  });

  // Precipitation type = snowflake icon
  map.addLayer({
    id: "precipitation",
    type: "symbol",
    source: "frost-live",
    layout: {
      "icon-image": ["case", ["==", ["get", "precipitation_type"], "snow"], "snowflake", ""],
      "icon-size": 0.5,
    },
  });

  // Wind speed = rotating icon
  map.addLayer({
    id: "wind",
    type: "symbol",
    source: "frost-live",
    layout: {
      "icon-image": "wind",
      "icon-size": 0.4,
      "icon-rotate": ["*", ["get", "wind_speed"], 10],
    },
  });
}

export default Layers;
