// Transform Frost API JSON into GeoJSON
export function frostToGeoJSON(frostData) {
  if (!frostData || !frostData.data) return { type: "FeatureCollection", features: [] };

  return {
    type: "FeatureCollection",
    features: frostData.data.map((obs) => ({
      type: "Feature",
      geometry: {
        type: "Point",
        coordinates: [obs.geometry.coordinates[0], obs.geometry.coordinates[1]],
      },
      properties: {
        snow_depth: obs.snow_depth || 0,
        precipitation_type: obs.precipitation_type || "none",
        wind_speed: obs.wind_speed || 0,
      },
    })),
  };
}
