import React, { useEffect, useState } from "react";
import { fetchStations, fetchObservations } from "./api/frostApi";
import { frostToGeoJSON } from "./utils/transform";
import MapView from "./components/MapView";
import Legend from "./components/Legend";

function App() {
  const [geojson, setGeojson] = useState(null);

  useEffect(() => {
    async function loadData() {
      try {
        // Step 1: get stations
        const stationData = await fetchStations();
        const stationIds = stationData.data.map((s) => s.id);

        // Limit to first 50 stations to avoid overload while testing
        const subset = stationIds.slice(0, 50);

        // Step 2: get latest observations
        const obsData = await fetchObservations(subset);

        // Step 3: transform into GeoJSON
        const geo = frostToGeoJSON(obsData);
        setGeojson(geo);
      } catch (err) {
        console.error("Error loading Frost data", err);
      }
    }

    loadData();
    const interval = setInterval(loadData, 300000); // refresh every 5 min
    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <MapView geojson={geojson} />
      <Legend />
    </div>
  );
}

export default App;
