function Layers(map) {
  // Snow depth circles
  map.addLayer({
    id: "snow-depth",
    type: "circle",
    source: "frost-live",
    paint: {
      "circle-radius": ["interpolate", ["linear"], ["get", "snow_depth"], 0, 2, 100, 20],
      "circle-color": [
        "match",
        ["get", "precipitation_type"],
        "snow",
        "#00ffff", // cyan for snow
        "rain",
        "#0000ff", // blue for rain
        "#ffffff"  // white default
      ],
      "circle-opacity": 0.7,
      "circle-stroke-width": 1,
      "circle-stroke-color": "#333"
    },
  });

  // Optional: wind speed = bigger stroke (no icon needed)
  map.addLayer({
    id: "wind-effect",
    type: "circle",
    source: "frost-live",
    paint: {
      "circle-radius": ["interpolate", ["linear"], ["get", "wind_speed"], 0, 0, 20, 10],
      "circle-color": "transparent",
      "circle-stroke-width": 2,
      "circle-stroke-color": "#888"
    },
  });
}

export default Layers;
