useEffect(() => {
  if (!mapObj.current || !geojson) return;

  // Wait until style is fully loaded
  if (!mapObj.current.isStyleLoaded()) {
    mapObj.current.once("styledata", () => {
      addOrUpdateSource();
    });
  } else {
    addOrUpdateSource();
  }
}, [geojson]);

function addOrUpdateSource() {
  if (mapObj.current.getSource("frost-live")) {
    mapObj.current.getSource("frost-live").setData(geojson);
  } else {
    mapObj.current.addSource("frost-live", { type: "geojson", data: geojson });
    mapObj.current.addLayer({
      id: "frost-layer",
      type: "circle",
      source: "frost-live",
      paint: {
        "circle-radius": 5,
        "circle-color": "#007cbf",
        "circle-stroke-width": 1,
        "circle-stroke-color": "#fff",
      },
    });
  }
}
