import React, { useEffect, useRef } from "react";
import mapboxgl from "mapbox-gl";
import Layers from "./Layers";
import SnowOverlay from "./SnowOverlay";

mapboxgl.accessToken = process.env.REACT_APP_MAPBOX_TOKEN;

function MapView({ geojson }) {
  const mapRef = useRef(null);
  const mapObj = useRef(null);

  useEffect(() => {
    mapObj.current = new mapboxgl.Map({
      container: mapRef.current,
      style: "mapbox://styles/mapbox/outdoors-v12",
      center: [10, 64.5],
      zoom: 4.2,
      pitch: 60,
      bearing: -20,
      antialias: true,
    });

    mapObj.current.addControl(new mapboxgl.NavigationControl(), "top-right");

    mapObj.current.on("load", async () => {
      mapObj.current.addSource("mapbox-dem", {
        type: "raster-dem",
        url: "mapbox://mapbox.terrain-rgb",
        tileSize: 512,
        maxzoom: 14,
      });

      mapObj.current.setTerrain({ source: "mapbox-dem", exaggeration: 1.5 });

      mapObj.current.addLayer({
        id: "sky",
        type: "sky",
        paint: {
          "sky-type": "atmosphere",
          "sky-atmosphere-sun": [0.0, 90.0],
          "sky-atmosphere-sun-intensity": 15,
        },
      });

      await addIcons(mapObj.current);

      if (geojson) {
        mapObj.current.addSource("frost-live", { type: "geojson", data: geojson });
        Layers(mapObj.current);
      }
    });

    return () => mapObj.current.remove();
  }, []);

  useEffect(() => {
    if (mapObj.current && mapObj.current.getSource("frost-live")) {
      mapObj.current.getSource("frost-live").setData(geojson);
    }
  }, [geojson]);

  return (
    <div style={{ position: "relative", width: "100%", height: "100vh" }}>
      <div ref={mapRef} style={{ width: "100%", height: "100%" }} />
      {mapObj.current && <SnowOverlay geojson={geojson} map={mapObj.current} />}
    </div>
  );
}

export default MapView;
