// src/components/WeatherStationsMap.jsx
import React, { useEffect, useRef } from "react";
import mapboxgl from "mapbox-gl";
import "mapbox-gl/dist/mapbox-gl.css";
import { fetchStations } from "./pages/frostAPI";
import { frostToGeoJSON } from "./pages/geojsonUtils";

mapboxgl.accessToken =
  "pk.eyJ1IjoibWFwZmVhbiIsImEiOiJjbTNuOGVvN3cxMGxsMmpzNThzc2s3cTJzIn0.1uhX17BCYd65SeQsW1yibA";

const WeatherStationsMap = () => {
  const mapContainer = useRef(null);
  const mapRef = useRef(null);

  useEffect(() => {
    const initMap = async () => {
      if (mapRef.current) return;

      console.log("🗺️ Initializing Mapbox map...");

      mapRef.current = new mapboxgl.Map({
        container: mapContainer.current,
        style: "mapbox://styles/mapbox/streets-v11",
        center: [10.75, 59.91],
        zoom: 4,
      });

      await new Promise((resolve) => mapRef.current.on("load", resolve));
      console.log("🛰️ Mapbox map fully loaded");

      // Fetch Frost API station data
      const stations = await fetchStations();
      console.log(`📊 Total stations fetched: ${stations.length}`);

      if (stations.length === 0) {
        console.warn("⚠️ No stations returned from Frost API");
        return;
      }

      // ✅ Filter stations to only include desired countries
      const allowedCountries = ["SE", "NO", "FI", "SJ", "IS", "GL"];
      const filteredStations = stations.filter(
        (station) => allowedCountries.includes(station.country)
      );

      console.log(
        `📌 Showing ${filteredStations.length} stations after filtering`
      );

      const geojson = frostToGeoJSON(filteredStations);

      // ✅ Use Blob URL to avoid Mapbox rejecting large inline GeoJSON
      const blob = new Blob([JSON.stringify(geojson)], {
        type: "application/json",
      });
      const url = URL.createObjectURL(blob);

      if (!mapRef.current.getSource("stations")) {
        mapRef.current.addSource("stations", {
          type: "geojson",
          data: url,
        });
        console.log("📡 GeoJSON source added successfully");
      }

      if (!mapRef.current.getLayer("stations-layer")) {
        mapRef.current.addLayer({
          id: "stations-layer",
          type: "circle",
          source: "stations",
          paint: {
            "circle-radius": [
              "interpolate",
              ["linear"],
              ["zoom"],
              0, 3,
              5, 5,
              10, 8,
              15, 14,
            ],
            "circle-color": "#ff6600",
            "circle-stroke-width": 1,
            "circle-stroke-color": "#fff",
            "circle-opacity": 0.9,
          },
        });
        console.log("✅ Station layer added successfully");
      }

      setTimeout(() => {
        const source = mapRef.current.getSource("stations");
        if (source) {
          console.log("📡 Stations source confirmed by Mapbox:", source);
        } else {
          console.error("🚨 Mapbox rejected the GeoJSON source!");
        }
      }, 500);

      // Fit map to cover the region: Greenland to Finland/Iceland
      mapRef.current.fitBounds(
        [
          [-56, 57], // SW corner (Greenland)
          [32, 83],  // NE corner (Svalbard)
        ],
        { padding: 30 }
      );

      // Popup on station click
      mapRef.current.on("click", "stations-layer", (e) => {
        const props = e.features[0].properties;
        const coords = e.features[0].geometry.coordinates;

        new mapboxgl.Popup()
          .setLngLat(coords)
          .setHTML(`
            <strong>${props.name}</strong><br/>
            ID: ${props.id}<br/>
            Country: ${props.country}
          `)
          .addTo(mapRef.current);
      });

      // Change cursor on hover
      mapRef.current.on("mouseenter", "stations-layer", () => {
        mapRef.current.getCanvas().style.cursor = "pointer";
      });
      mapRef.current.on("mouseleave", "stations-layer", () => {
        mapRef.current.getCanvas().style.cursor = "";
      });

      // Handle Mapbox rendering errors explicitly
      mapRef.current.on("error", (e) => {
        console.error("🛑 Mapbox rendering error:", e.error);
      });
    };

    initMap();
  }, []);

  return (
    <div
      ref={mapContainer}
      style={{ width: "100%", height: "100vh", borderRadius: "10px" }}
    />
  );
};

export default WeatherStationsMap;
