// src/api/frostApi.js
import { frostToGeoJSON } from "../utils/transform";

// Fetch all available weather stations in Norway
export async function fetchStations() {
  const clientId = process.env.REACT_APP_FROST_CLIENT_ID;

  const res = await fetch(
    "https://frost.met.no/sources/v0.jsonld?types=SensorSystem&country=NO",
    {
      headers: {
        "Authorization": "Basic " + btoa(clientId + ":"),
        "Accept": "application/json",
      },
    }
  );

  if (!res.ok) throw new Error("Failed to fetch stations");
  return await res.json();
}

// Fetch observations for selected stations and return as GeoJSON
export async function fetchObservations(stationIds) {
  const clientId = process.env.REACT_APP_FROST_CLIENT_ID;
  const sources = stationIds.join(",");

  const url =
    `https://frost.met.no/observations/v0.jsonld` +
    `?sources=${sources}` +
    `&elements=snow_depth,precipitation_type,wind_speed` +
    `&referencetime=latest`;

  const res = await fetch(url, {
    headers: {
      "Authorization": "Basic " + btoa(clientId + ":"),
      "Accept": "application/json",
    },
  });

  if (!res.ok) throw new Error("Failed to fetch observations");

  const frostData = await res.json();

  // Transform into GeoJSON so MapView can use it directly
  return frostToGeoJSON(frostData);
}
