import React, { useEffect, useRef } from "react";
import mapboxgl from "mapbox-gl";
import "mapbox-gl/dist/mapbox-gl.css";

// Directly use your Mapbox token
mapboxgl.accessToken =
  "pk.eyJ1IjoibWFwZmVhbiIsImEiOiJjbTNuOGVvN3cxMGxsMmpzNThzc2s3cTJzIn0.1uhX17BCYd65SeQsW1yibA";

// Frost API credentials (hardcoded)
const FROST_CLIENT_ID = "12f68031-8ce7-48c7-bc7a-38b843f53711";
const FROST_CLIENT_SECRET = "08a75b8d-ca70-44a9-807d-d79421c082bf";

const WeatherStationsMap = () => {
  const mapContainer = useRef(null);
  const mapRef = useRef(null);

  // Convert Frost API response into GeoJSON
  const frostToGeoJSON = (stations) => ({
    type: "FeatureCollection",
    features: stations
      .filter((station) => station.geometry) // Ignore stations without coordinates
      .map((station) => ({
        type: "Feature",
        geometry: {
          type: "Point",
          coordinates: station.geometry.coordinates,
        },
        properties: {
          id: station.id,
          name: station.name,
          shortName: station.shortName || "Unknown",
          country: station.country || "Norway",
        },
      })),
  });
// Fetch stations from Frost API via local proxy
const fetchStations = async () => {
  try {
    const frostAuth = btoa(`${FROST_CLIENT_ID}:${FROST_CLIENT_SECRET}`);

    // Use the /frost proxy instead of calling frost.met.no directly
    const response = await fetch(
      "/frost/sources/v0.jsonld?types=SensorSystem",
      {
        headers: {
          Authorization: `Basic ${frostAuth}`,
        },
      }
    );

    if (!response.ok) {
      throw new Error(`Frost API error: ${response.status}`);
    }

    const data = await response.json();
    return data.data || [];
  } catch (error) {
    console.error("Error fetching Frost data:", error);
    return [];
  }
};

  // Initialize Mapbox and add stations
  useEffect(() => {
    const initMap = async () => {
      if (mapRef.current) return;

      // Initialize Mapbox map
      mapRef.current = new mapboxgl.Map({
        container: mapContainer.current,
        style: "mapbox://styles/mapbox/streets-v11",
        center: [10.75, 59.91], // Oslo
        zoom: 5,
      });

      // Fetch stations
      const stations = await fetchStations();
      const geojson = frostToGeoJSON(stations);

      // Add station markers to map
      mapRef.current.on("load", () => {
        mapRef.current.addSource("stations", {
          type: "geojson",
          data: geojson,
        });

        mapRef.current.addLayer({
          id: "stations-layer",
          type: "circle",
          source: "stations",
          paint: {
            "circle-radius": 5,
            "circle-color": "#007cbf",
            "circle-stroke-width": 1,
            "circle-stroke-color": "#fff",
          },
        });

        // Add popups on click
        mapRef.current.on("click", "stations-layer", (e) => {
          const props = e.features[0].properties;
          const coords = e.features[0].geometry.coordinates;

          new mapboxgl.Popup()
            .setLngLat(coords)
            .setHTML(`
              <strong>${props.name}</strong><br/>
              ID: ${props.id}<br/>
              Country: ${props.country}
            `)
            .addTo(mapRef.current);
        });

        // Change cursor when hovering stations
        mapRef.current.on("mouseenter", "stations-layer", () => {
          mapRef.current.getCanvas().style.cursor = "pointer";
        });

        mapRef.current.on("mouseleave", "stations-layer", () => {
          mapRef.current.getCanvas().style.cursor = "";
        });
      });
    };

    initMap();
  }, []);

  return (
    <div
      ref={mapContainer}
      style={{ width: "100%", height: "100vh", borderRadius: "10px" }}
    />
  );
};

export default WeatherStationsMap;
