// src/utils/transform.js

/**
 * Converts Frost API data into valid GeoJSON for Mapbox.
 * @param {Object} frostData - Frost API JSON response
 * @returns {Object} GeoJSON FeatureCollection
 */
export function frostToGeoJSON(frostData) {
  if (!frostData || !frostData.data) {
    return { type: "FeatureCollection", features: [] };
  }

  return {
    type: "FeatureCollection",
    features: frostData.data
      .filter(obs => obs.geometry?.coordinates?.length === 2)
      .map(obs => {
        const props = {};
        obs.observations?.forEach(o => {
          props[o.elementId] = o.value;
        });

        return {
          type: "Feature",
          geometry: {
            type: "Point",
            coordinates: obs.geometry.coordinates,
          },
          properties: {
            ...props,
            sourceId: obs.sourceId,
            referenceTime: obs.referenceTime,
          },
        };
      }),
  };
}
